mapper.update:
update t_score
pst_score = #{pstScore}, pst_ptt = #{pstPtt}, pst_score = null, pst_ptt = null, prs_score = #{prsScore}, prs_ptt = #{prsPtt}, prs_score = null, prs_ptt = null, ftr_score = #{ftrScore}, ftr_ptt = #{ftrPtt}, ftr_score = null, ftr_ptt = null, byd_score = #{bydScore}, byd_ptt = #{bydPtt} byd_score = null, byd_ptt = null, etr_score = #{etrScore}, etr_ptt = #{etrPtt} etr_score = null, etr_ptt = null, time = now(),
where scid = #{scid}

mapper.select:
select tsc.pst_score, tsc.prs_score, tsc.ftr_score, tsc.byd_score, tsc.etr_score
from t_score tsc
where tsc.sid = (
    select ts.sid from t_song ts where ts.sgid = #{param1}
) and uid = #{param2}

select ts.sname, ts.pst, ts.prs, ts.ftr, ts.byd, ts.etr, ts.pid, tsc.*
from t_score tsc, t_song ts
where tsc.uid = #{userId} and tsc.sid = ts.sid;
